        return (<option key={i} value={key.value}>{key.country_name}</option>)

})
}


</select>
<select value={cities} onChange={weatherapicall} >
    {cities.map((key,i)=>(<option key={i} value={key}>{key}</option>))}


</select>
</>
        );
}
