import React from 'react';
import { useState } from 'react';
function Dropdownmenu(){
    const countries_cities =[
       { country_name: "pakistan" ,value:"pakistan" , cities:["karachi","islamabad","lahore"]},
       { country_name: "usa" ,value:"usa" , cities:["chicago","houston","new york"]}

    ];
    const[countries,setCountries] = useState("countries");
    const[cities,setCities]=useState([]);

function weatherapicall(event){
 setCountries(event.target.value);
 setCities(countries_cities.find(data=>data.value===event.target.value).cities);
 console.log(cities);
}

    return(
        <>
<select value={countries} onChange={weatherapicall}>
    {countries_cities.map((key,i)=>(<option key={i} value={key.value}>{key.country_name}</option>))}


</select>
<h1>{cities}</h1>
</>
        );
}
export default Dropdownmenu;
